jQuery.fn.kounty = function() {
    return this.each(function() {
        const  __this = jQuery(this);

            var countContainer = jQuery(this),
                myTimer = countContainer.attr('data-timer-date'),
                countDownDate = new Date(myTimer).getTime(),
                countMessage = countContainer.attr('data-countdown-message'),
                countlabel = jQuery(this).attr('data-countdown-seperator'),

                sTimerfunction = setInterval(function () {
                    var cDate = new Date().getTime();
                    var kountdis = countDownDate - cDate;
                    var days = Math.floor(kountdis / (1000 * 60 * 60 * 24));
                    var hours = Math.floor((kountdis % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
                    var minutes = Math.floor((kountdis % (1000 * 60 * 60)) / (1000 * 60));
                    var seconds = Math.floor((kountdis % (1000 * 60)) / 1000);
                    function labelFunction(){
                        jQuery.fn.hasAttr = function(name) {  
                           return this.attr(name) !== undefined && this.attr(name) !== '';
                        };
                        var daylabel,hourlabel,minutelabel,secondlabel;
                        if(jQuery(__this).hasAttr('data-day-label')){
                            daylabel = jQuery(__this).attr('data-day-label');
                            __this.children(".countdown-days").html(days).append(`<span>${daylabel}</span>`);
                        }
                        if(jQuery(__this).hasAttr('data-hour-label')){
                            hourlabel = jQuery(__this).attr('data-hour-label');
                             __this.children(".countdown-hours").html(hours).append(`<span>${hourlabel}</span>`);
                        }
                        if(jQuery(__this).hasAttr('data-min-label')){
                            minutelabel = jQuery(__this).attr('data-min-label');
                             __this.children(".countdown-minutes").html(minutes).append(`<span>${minutelabel}</span>`);
                        }
                        if(jQuery(__this).hasAttr('data-sec-label')){
                            secondlabel = jQuery(__this).attr('data-sec-label');
                             __this.children(".countdown-seconds").html(seconds).append(`<span>${secondlabel}</span>`);
                        }
                    }
    
                    if (countlabel === 'dots') {
                        jQuery(__this).addClass('countdown-dots');
                        countContainer.addClass('countdown-dots');
                        countContainer.children(".countdown-days").html(days).append(`<span>:</span>`);
                        countContainer.children(".countdown-hours").html(hours).append(`<span>:</span>`);
                        countContainer.children(".countdown-minutes").html(minutes).append(`<span>:</span>`);
                        countContainer.children(".countdown-seconds").html(seconds).append(`<span>:</span>`);
                    }
                    else if (countlabel === 'label') {
                        jQuery(__this).addClass('countdown-label');
                        labelFunction()
                    } else {
                        countContainer.children(".countdown-days").html(days).append("<span> days </span>");
                        countContainer.children(".countdown-hours").html(hours).append("<span> hours </span>");
                        countContainer.children(".countdown-minutes").html(minutes).append("<span> minutes </span>");
                        countContainer.children(".countdown-seconds").html(seconds).append("<span> seconds </span>");
                    }
                    if (kountdis < 0) {
                        clearInterval(sTimerfunction);
                        countContainer.html(countMessage);
                        countContainer.addClass('countdown-finished');
                    } else {
                        countContainer.removeClass('countdown-finished');
                    }
                }, 1000);
    });
 
};